<template>
 <div class="playlist">
   <img :src='selectedSong.url' alt="">
  <h1>{{selectedSong.title}}</h1>
  <youtube :video-id="selectedSong.videoId" :player-vars="playerVars" @ended="ended" ref="youtube" @paused="isPlaying('stop playing')" @playing="isPlaying('playing')" @change.native="test"></youtube>
   <ul>
     <li v-for="(song, idx) in songs" :key="song.id" @click="playSong(idx)">{{song.title}}</li>
   </ul>
   <div class="control-panel">
     {{progress}}
     <button @click="play">play</button>
     <button @click="stop">pause</button>
     <button @click="playSong(currSongNum+1)">next</button>
     <button @click="playSong(currSongNum-1)">prev</button>
     <button @click="count()">time</button>
   </div>
  </div>
</template>

<script>
export default {
  data() {
    return {
      progressBar: 'pause',
      playerVars: {
        autoplay: 1
      },
      currSongNum: 0,
      selectedSong: {
        id:323,
        videoId: 'lG0Ys-2d4MA',
        title: 'madona',
        url:'https://static.wixstatic.com/media/87e304_eaf832f41c044363abed318aa0048418~mv2.jpg_256'
      },
      songs:[
           {id:323 ,videoId: 'lG0Ys-2d4MA', title: 'madona', url:'data:image/jpeg;base64,'},
           {id:3443 ,videoId: 'qqIIW7nxBgc', title: 'elen', url:'https://i.pinimg.com/originals/79/d1/3e/79d13edc13f8675e6232a7143069a905.jpg'},
           {id:3653 ,videoId: 'oUbggm0E7Dw', title: 'david', url:'https://static.wixstatic.com/media/87e304_eaf832f41c044363abed318aa0048418~mv2.jpg_256'}
      ]
    }
  },
  mounted() {
   this.player.setSize(0, 0)
  },
  computed: {
    player () {
      return this.$refs.youtube.player
    },
    progress(){
       return this.progressBar
    }
  },
  methods: {
     stop(){
      this.player.pauseVideo()
     },
     play(){
      this.player.playVideo()
     },
    ended() {
       if(this.currSongNum+1 <= this.songs.length){
       this.selectedSong = this.songs[this.currSongNum+1];
       this.currSongNum = this.currSongNum+1;
       } else {
        this.selectedSong = this.songs[0];
        this.currSongNum = 0;
       }
    },
    playSong(songIdx) {
      if(songIdx < 0) return;
      if(songIdx > this.songs.length-1) return;
      this.selectedSong = this.songs[songIdx];
      this.currSongNum = songIdx;
    },
    getTime(){
      console.log(this.player.getCurrentTime())
    },
    isPlaying(input){
      this.progressBar=input;
    }
  }
}
</script>

<style scoped>
.control-panel{
  background: black;
}

img{
  height: 100px;
  width: 100px;
}

</style>
